/**
 * @module logic/camera-icon-renderer.js
 */
var Renderer = require("logic/map/renderer").Renderer,
    EsriGeometryType = require("logic/map/esri-geometry-type").EsriGeometryType;

var cameraIconUrl = "",
    enabledCameraIconUrl = "",
    disabledCameraIconUrl = "";

/**
 * @class CameraIconRenderer
 * @extends Renderer
 *
 * This object is used by the cameras layer to display its icons.
 */
exports.CameraIconRenderer = Renderer.specialize(/** @lends CameraIconRenderer.prototype */ {

    constructor: {
        value: function CameraIconRenderer() {
            this.geometryType = EsriGeometryType["esriGeometryPoint"];
        }
    },

    _renderPoint: {
        value: function (camera) {

            var url = Renderer.symbolUrl({
                    contentType: "image/png",
                    imageData: this._url(camera)
                }),
                dimensions = Renderer.getDimensions(url);

            return {
                anchor: {
                    x: parseInt(dimensions.width / 2),
                    y: parseInt(dimensions.height / 2)
                },
                size: {
                    width: dimensions.width,
                    height: dimensions.height
                },
                url: url
            };
        }
    },

    _url: {
        value: function (camera) {
            var url;
            if (camera.isEnabled) {
                url = enabledCameraIconUrl;
            } else if (camera.active === 1) {
                url = cameraIconUrl;
            } else {
                url = disabledCameraIconUrl;
            }
            return url;
        }
    }

});
