var Montage = require("montage").Montage;

/**
 * @class HazardType
 * @extends Montage
 *
 * Object representing a hazard type.
 *
 */
var HazardType = exports.HazardType = Montage.specialize(/** @lends HazardType.prototype */ {

    constructor: {
        value: function HazardType() {
        }
    }

}, {

    initialize: {
        value: function (data) {
            var hazardType = new HazardType();
            hazardType.id = data.type_id;
            hazardType.icon = data.type_icon;
            hazardType.name = data.type_name;
            return hazardType;
        }
    }

});
