/**
 * @module logic/feature-selector.js
 */
var ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class FeatureSelector
 * @extends ObjectSelector
 */
exports.FeatureSelector = ObjectSelector.specialize(/** @lends FeatureSelector.prototype */ {

    /**
     * Feature Selector may provide the following attributes
     * extent - an area of the map
     * srid - the projection system
     * units - the measurement system e.g. meters or decimal degrees
     *
     */
    constructor: {
        value: function FeatureSelector(layer, extent, projection, units) {
            this.super("feature");
            this.layer = layer;
            this.extent = extent;
            this.projection = projection;
            this.units = units;
        }
    }
});
