/**
 * @module logic/hazard-severity-selector.js
 */
var ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class HazardSeveritySelector
 * @extends ObjectSelector
 */
exports.HazardSeveritySelector = ObjectSelector.specialize(/** @lends HazardSeveritySelector.prototype */ {

    /**
     * HazardSeveritySelector is a selector used to access the mock hazard severity service.
     */
    constructor: {
        value: function HazardSeveritySelector(severityId) {
            this.super("hazardSeverity");
            this.severityId = severityId;
        }
    }
});
