/**
 * @module logic/hazard-type-selector.js
 */
var ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class HazardTypeSelector
 * @extends ObjectSelector
 */
exports.HazardTypeSelector = ObjectSelector.specialize(/** @lends HazardTypeSelector.prototype */ {

    /**
     * HazardTypeSelector is a selector used to access the mock hazard type service.
     */
    constructor: {
        value: function HazardTypeSelector(serviceUrl, typeId) {
            this.super("hazardType");
            this.serviceUrl = serviceUrl;
            this.typeId = typeId;
        }
    }
});
