/**
 * @module logic/product-selector.js
 */
var ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class ProductSelector
 * @extends ObjectSelector
 */
exports.ProductSelector = ObjectSelector.specialize(/** @lends ProductSelector.prototype */ {

    /**
     * ProductSelector is a selector used to access the mock product service.
     */
    constructor: {
        value: function ProductSelector(hazard) {
            this.super("product");
            this.hazard = hazard;
        }
    }
});
