/**
 * @module logic/tile-selector.js
 */
var ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class TileSelector
 * @extends ObjectSelector
 *
 * Object defining a query for a map tile image.
 * Eventually this tile selector class will be generated
 * dynamically from a blueprint.
 */
exports.TileSelector = ObjectSelector.specialize(/** @lends TileSelector.prototype */ {
    constructor: {
        value: function TileSelector(layer, tileId) {
            this.super("tile");
            this.layer = layer;
            this.tileId = tileId;
        }
    }
});
