/**
 * @module logic/TimeZone-service.js
 */
var ObjectService = require("logic/mock-montage-data/object-service").ObjectService,
    ObjectStream = require("logic/mock-montage-data/object-stream").ObjectStream;

/**
 * @class TimeZoneService
 * @extends ObjectService
 */
exports.TimeZoneService = ObjectService.specialize(/** @lends TimeZoneService.prototype */ {
    constructor: {
        value: function TimeZoneService() {
            this.super("TimeZone");
        }
    },

    _mockCountriesAndCities: {
        value: [
            {country: "United States", cities: ["Washington, D.C.", "Honolulu"]},
            {country: "Japan", cities: ["Tokyo"]},
            {country: "Australia", cities: ["Sydney"]},
            {country: "United Kingdom", cities: ["London"]}
        ]
    },

    query: {
        value: function (selector) {
            var self = this;
            var stream = new ObjectStream(selector);
            setTimeout(function () {
                stream.add(self._mockCountriesAndCities);
                stream.resolve(self._mockCountriesAndCities);
            }, 0);
            return stream;
        }
    }
});
