/**
 * @module logic/brief.js
 */
var Montage = require("montage").Montage;

/**
 * @class BriefTab
 * @extends Montage
 */
exports.BriefTab = Montage.specialize(/** @lends BriefTab.prototype */ {
    constructor: {
        value: function BriefTab(id, name, disclosure) {
            this.id = id;
            this.name = name;
            this.disclosure = disclosure;
        }
    }
});

/**
 * @class Brief
 * @extends Montage
 */
exports.Brief = Montage.specialize(/** @lends Brief.prototype */ {
    constructor: {
        value: function Brief(templateId, tabs) {
            this.templateId = templateId;
            this.tabs = tabs;
        }
    }
});


