var Montage = require("montage").Montage;

/**
 * Feature property descriptor.
 *
 * @class Field
 * @extends external:Montage
 */
exports.Field = Montage.specialize(/** @lends Field# */ {

    name: {
        value: undefined
    },

    propertyName: {
        value: undefined
    },

    formatter: {
        value: undefined
    },

    // Shim to match the mock model API.

    label: {
        get: function () {
            return this.name;
        }
    },

    key: {
        get: function () {
            return this.propertyName;
        }
    },

    type: {
        value: null
    },

    filter: {
        value: {}
    }


}, {

    create: {
        value: function (name, propertyName, formatter) {
            var field = new (this)();
            field.name = name;
            field.propertyName = propertyName;
            field.formatter = formatter;
            return field;
        }
    }

});
