var Enumeration = require("montage-data/logic/model/enumeration").Enumeration;

/**
 * One of the types of geometries used by [Layers]{@link Layer}.
 *
 * Geometry type names are taken from GeoJSON.
 *
 * @class
 * @extends external:Enumeration
 */
exports.GeometryType = Enumeration.specialize("esriId", "name", /** @lends GeometryType */ {

    /** @type {GeometryType} */
    POINT: ["esriGeometryPoint", "Point"],

    /** @type {GeometryType} */
    MULTI_POINT: ["esriGeometryMultipoint", "Multi-Point"],

    /** @type {GeometryType} */
    MULTI_LINE_STRING: ["esriGeometryPolyline", "Multi-Line String"],

    /** @type {GeometryType} */
    POLYGON: ["esriGeometryPolygon", "Polygon"],

    /** @type {GeometryType} */
    RASTER: ["", "Raster"]

});
