/**
 * @module logic/link-category.js
 */
var Montage = require("montage").Montage,
    Link = require("logic/model/link").Link;

/**
 * @class LinkCategory
 * @extends Montage
 */
exports.LinkCategory = Montage.specialize(/** @lends LinkCategory.prototype */ {

    constructor: {
        value: function LinkCategory() {
        }
    }

}, {

    initialize: {
        value: function (data) {
            var category = {},
                linksData = data.getElementsByTagName('link'),
                i, length;

            category.name = data.getAttribute("name");
            category.links = [];

            for (i = 0, length = linksData.length; i < length; i++) {
                category.links.push(Link.initialize(linksData[i]));
            }

            return category;
        }
    }
});
