/**
 * @module logic/link.js
 */
var Montage = require("montage").Montage;

/**
 * @class Link
 * @extends Montage
 */
exports.Link = Montage.specialize(/** @lends Link.prototype */ {

    constructor: {
        value: function Link() {
        }
    }

}, {

    initialize: {
        value: function (data) {
            var link = {};

            link.display = data.getElementsByTagName('display')[0].innerHTML;
            link.url = data.getElementsByTagName('url')[0].innerHTML;
            link.target = data.getElementsByTagName('target')[0].innerHTML;

            return link;
        }
    }
});
