var Montage = require("montage").Montage,
    ObjectDescriptor = require("montage-data/logic/model/object-descriptor").ObjectDescriptor;

/**
 * @class
 * @extends external:Montage
 */
var User = exports.User = Montage.specialize(/** @lends User# */ {

    authorizationService: {
        value: undefined
    },

    roles: {
        value: undefined
    }

}, {

    TYPE: {
        get: ObjectDescriptor.getterFor(exports, "User")
    },

    initialize: {
        value: function (data) {
            var user = new User();
            user.languages = data.languages;
            user.timeZones = data.timeZones;
            user.bookmarks = data.bookmarks;
            user.saveHazardFilter = data.saveHazardFilter;
            user.notificationSound = data.notificationSound;
            return user;
        }
    }

});
