var DataService = require("montage-data/logic/service/data-service").DataService,
    DataMapping = require("montage-data/logic/service/data-mapping").DataMapping,
    User = require("logic/model/user").User;

/**
 * Mock user service that currently only provides mock roles.
 *
 * @class
 * @extends external:DataService
 */
exports.UserService = DataService.specialize(/** @lends UserService.prototype */{

    /***************************************************************************
     * Basic properties
     */

    type: {
        value: User.TYPE
    },

    authority: {
        value: undefined
    },

    /***************************************************************************
     * Entry point
     */

    fetchRawData: {
        value: function (stream) {
            // Creates mock data for now.
            var user = new User();
            user.authority = this.authority;
            user.roles = {"HI_ANIMALHEALTH": true};
            this.addRawData(stream, [user]);
            this.rawDataDone(stream);
        }
    }

});
