/**
 * @module ui/bookmark/bookmark-data-summary.reel
 */
var Component = require("montage/ui/component").Component,
    RangeController = require("montage/core/range-controller").RangeController;

/**
 * @class BookmarkDataSummary
 * @extends Component
 */
exports.BookmarkDataSummary = Component.specialize(/** @lends BookmarkDataSummary# */ {
    constructor: {
        value: function BookmarkDataSummary() {

        }
    },
    bookmark: {
        set: function (value) {
            var self = this, bookmarkData;
            if (value) {
                value.data.then(function (data) {
                    bookmarkData =  data[0];
                    self._hasLayers = bookmarkData.layers && Array.isArray(bookmarkData.layers)
                    if (self._hasLayers) {
                        if (self._layersController) {
                            self._layersController.clear();
                        } else {
                            self._layersController = self._layersController || new RangeController();
                        }
                        self._layersController.addEach(bookmarkData.layers);
                    }
                    self._hasExtent = bookmarkData.extent && (self._extent = bookmarkData.extent);
                });
            }
        }
    },
    _extent: {
        value: {
            center: null,
            zoom: null
        }
    },
    _hasExtent: {
        value: false
    },
    _hasLayers: {
        value: false
    },
    _layersController: {
        value: null
    }
});
