/**
 * @module ui/bookmark/bookmark-list-item.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class BookmarkListItem
 * @extends Component
 */
exports.BookmarkListItem = Component.specialize(/** @lends BookmarkListItem# */ {
    constructor: {
        value: function BookmarkListItem() {

        }
    },
    bookmark: {
        value: null
    },
    _creationDate: {
        set: function(textComponent) {
            textComponent.converter = this._dateConverter;
        }
    },
    _dateConverter: {
        value: {
            convert: function(input) {
                return input ? input.format("M.DD.YYYY H\\h:mm\\m") : "";
            }
        }
    }
});
