/**
 * @module ui/brief-list-item.reel
 */
var Component = require("montage/ui/component").Component,
    PressComposer = require("montage/composer/press-composer").PressComposer;

/**
 * @class BriefListItem
 * @extends Component
 */
exports.BriefListItem = Component.specialize(/** @lends BriefListItem# */ {

    area: {
        value: undefined
    },

    constructor: {
        value: function BriefListItem() {

        }
    },

    prepareForActivationEvents: {
        value: function () {
            this._pressComposer = new PressComposer();
            this.addComposer(this._pressComposer);
            this._pressComposer.addEventListener("press", this);
        }
    },

    handlePress: {
        value: function (event) {
            event.stopPropagation();
            this.dispatchEventNamed("selectAreaBriefAction", true, true, this.area);
        }
    }

});
