/**
 * @module ui/brief-list.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class BriefList
 * @extends Component
 */
exports.BriefList = Component.specialize(/** @lends BriefList.prototype */ {

    _loading: {
        value: false
    },

    loading: {
        set: function (loading) {
            this.statusMessage = "Report " + (this.briefAreas.length + 1);
            this._loading = loading;
        },
        get: function () {
            return this._loading;
        }
    },

    briefAreas: {
        value: []
    },

    statusMessage: {
        value: ""
    },

    handleMenuAction: {
        value: function (event) {
            if (event.detail.item.value === "remove") {
                var delegate = this.application.delegate;

                delegate.briefAreas = delegate.briefAreas.filter(function (item) {
                    return !item.isChecked;
                });

                this.briefAreas = delegate.briefAreas.slice(0);
            }
        }
    }
});
