/**
 * @module ui/cameraListItem.reel
 */
var Component = require("montage/ui/component").Component;
/**
 * @class CameraListItem
 * @extends Component
 */
exports.CameraListItem = Component.specialize(/** @lends CameraListItem# */ {
    constructor: {
        value: function CameraListItem() {

        }
    },

    _camera: {
        value: null
    },

    camera: {
        set: function (value) {
            if (value && value !== this._camera) {
                this._camera = value;
            }
        },
        get: function () {
            return this._camera;
        }
    }

});
