var Button = require("ui/base/button.reel").Button;

/**
 * A component that provides a styled button
 * @class ActionButton
 * @extends external:Component
 */
exports.ActionButton = Button.specialize(/** @lends ActionButton# */ {

    hasTemplate: {
        value: true
    },

    _type: {
        get: function () {
            return this.getPath("type") || "button";
        }
    }

});
