/**
 * @module ui/control/coordinate-display.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class CoordinateDisplay
 * @extends Component
 */
exports.CoordinateDisplay = Component.specialize(/** @lends CoordinateDisplay# */ {
    constructor: {
        value: function CoordinateDisplay() {

        }
    },

    _latitude: { value: null },

    _longitude: { value: null },

    latitude: {
        get: function () {
            return this._latitude;
        },
        set: function (value) {
            this._latitude = value;
        }
    },

    longitude: {
        get: function () {
            return this._longitude;
        },
        set: function (value) {
            this._longitude = value;

        }
    },

    latitudeString: {
        get: function () {
            var string;
            if (!this.latitude) {
                string = "";
            } else {
                string = String(Math.abs(this.latitude.toFixed(2)));
                string += " ";
                string += this.latitude >= 0 ? "N" : "S";
            }
            return string;
        }
    },

    longitudeString: {
        get: function () {
            var string;
            if (!this.longitude) {
                string = "";
            } else {
                string = String(Math.abs(this.longitude.toFixed(2)));
                string += " ";
                string += this.longitude >= 0 ? "E" : "W";
            }
            return string;
        }
    }

});
