/**
 * @module ui/main.reel
 */
var Component = require("montage/ui/component").Component,
    moment = require("contour-framework/moment-timezone");

/**
 * @class Main
 * @extends Component
 */
exports.Main = Component.specialize(/** @lends Main# */ {
    constructor: {
        value: function Main() {

            this.moment = moment();
        }
    },

    moment: {
        set: function (value) {
            if (this._moment == value) {
                //Moment properties may change
                this.dispatchOwnPropertyChange("moment", this.moment);
            } else {
                this._moment = value;
            }
        },
        get: function () {
            return this._moment;
        }
    },

    _timeZone: {
        value: 'US/Pacific'
    },

    timeZone: {
        get: function () {
            return this._timeZone;
        },
        set: function (value) {
            this._timeZone = value;
        }
    },

    _locale: {
        value: 'en-US'
    },

    locale: {
        get: function () {
            return this._locale;
        },
        set: function (value) {
            this._locale = value;
        }
    },

    handleAction: {
        value: function () {
            this.moment = this.moment.clone().add(1, 'days');
        }
    }
});
