/**
 * @module ui/console.reel
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component;

/**
 * @class Console
 * @extends Component
 */
exports.Console = Component.specialize(/** @lends Console.prototype */ {

    constructor: {
        value: function Console() {

        }
    },

    _log: {
        value: false
    },

    log: {
        value: function (action) {
            this._log = this._log || [];
            this._log.push(action);
            this.element.scrollTop = this.element.scrollHeight;
        }
    }

});
