/**
 * @module ui/main.reel
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component;

/**
 * @class Main
 * @extends Component
 */
exports.Main = Component.specialize(/** @lends Main.prototype */ {

    constructor: {
        value: function Main() {

        }
    },

    _preparedForAction: {
        value: false
    },

    prepareForActivationEvents: {
        value: function () {
            if (!this._preparedForAction) {
                this.addEventListener("headingAction", this, false);
                this._preparedForAction = true;
            }
        }
    },

    handleHeadingAction: {
        value: function (event) {
            var heading = event.detail.headingBar,
                label = heading.actionLabel,
                console = this.templateObjects[heading.identifier + "Console"];
            (console || window.console).log(label);
            if (heading.alternativeActionLabel) {
                heading.actionLabel = heading.alternativeActionLabel;
                heading.alternativeActionLabel = label;
            }
        }
    }
});
