var Component = require("montage/ui/component").Component;

exports.Main = Component.specialize({

    prepareForActivationEvents: {
        value: function () {
            this.textField3HeadingBar.addEventListener("headingAction", this, false);
            this.textArea2HeadingBar.addEventListener("headingAction", this, false);
            this.textField2.addEventListener("action", this, false);
        }
    },

    handleAction: {
        value: function (e) {
            e.stopPropagation();
            if (e.target && e.target.value) {
                this.textField2Display.value = e.target.value;
            }
        }
    },

    handleHeadingAction: {
        value: function (e) {
            e.stopPropagation();
            if (e.target && e.target.input) {
                e.target.input.enabled = !e.target.input.enabled;
            }
        }
    }
});
