var Component = require("montage/ui/component").Component;

/**
 * @class Label
 * @extends Component
 */
exports.Label = Component.specialize(/** @lends Label.prototype */{
    // properties =======================

    isActive: {value: false},

    _enabled: {value: true},

    enabled: {
        get: function () {
            return this._enabled;
        },
        set: function (value) {
            if (this._enabled !== value) {
                this._enabled = value;
                this.needsDraw = true;
            }
        }
    },

    label: {value: null}

});
