/**
 * @module ui/main.reel
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component,
    MockService = require("contour-framework/logic/mock-service/main-service").MockService,
    ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class Main
 * @extends Component
 */
exports.Main = Component.specialize(/** @lends Main.prototype */ {
    constructor: {
        value: function Main() {

            var self = this;
            var service = new MockService();
            service.query(new ObjectSelector("layer")).then(function (layers) {
                self.layers = layers;
            });
        }
    },

    description: {
        value: 'No search text'
    },

    /*
     * Search bar delegate implement function
     */
    searchBarTextDidChange: {
        value: function (text, callback) {
            var self = this;
            self.description = 'Searching ' + text;
            setTimeout(function () {
                if(!text){
                    self.description = 'No search text'
                }else{
                    self.description = 'Search complete ' + text;
                }

                callback();
            }, 1000);
        }
    }
});
