var Component = require("montage/ui/component").Component;

/**
 * @class Slider
 * @extends Component
 */
exports.Slider = Component.specialize(/** @lends Slider.prototype */{

    // properties =======================

    // event handlers =======================

    // lifecycle hooks =======================

    draw: {
        value: function () {
            this._sliderFillElement.style.width =
                (this.value - this.min) / (this.max - this.min) * 100 + '%'
        }
    }

});
