var Component = require("montage/ui/component").Component;

/**
 * @class TabBarItem
 * @extends Component
 */
exports.TabBarItem = Component.specialize(/** @lends TabBarItem.prototype */ {

    tab: {
        value: {}
    },

    _icon: {
        set: function (icon) {
            if (icon !== this.__icon) {
                if (this.__icon && this.__iconClass) {
                    this.__icon.classList.delete(this.__iconClass);
                }
                if (icon && this.__iconClass) {
                    icon.classList.add(this.__iconClass);
                }
                this.__icon = icon;
            }
        }
    },

    _iconClass: {
        set: function (iconClass) {
            if (iconClass !== this.__iconClass) {
                if (this.__icon && this.__iconClass) {
                    this.__icon.classList.delete(this.__iconClass);
                }
                if (this.__icon && iconClass) {
                    this.__icon.classList.add(iconClass);
                }
                this.__iconClass = iconClass;
            }
        }
    }

});
