/**
 * @module ui/control/text-field.reel
 */
var TextField = require("ui/base/text-field.reel").TextField;

/**
 * @class TextField
 * @extends TextField
 */
exports.TextField = TextField.specialize(/** @lends TextField.prototype */ {
    autoFocus: { value: false },

    hasTemplate: { value: true },

    draw: {
        value: function () {
            this.element.disabled = !this.enabled;
            this.element.autofocus = this.autoFocus;
            if (this.element.value !== this.value) {
                this.element.value = this.value ? this.value : "";
            }
        }
    }
});
