/**
 * @module ui/figure-list-item.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class FigureListItem
 * @extends Component
 */
exports.FigureListItem = Component.specialize(/** @lends FigureListItem# */ {
    constructor: {
        value: function FigureListItem() {

        }
    },
    _figure: {
        value: null
    },
    figure: {
        set: function (value) {
            if (value && this._figure !== value) {
                this._figure = value;
            }
        },
        get: function () {
            return this._figure;
        }
    },
    _preview: {
        set: function(value) {
            value.needsDraw = true;
        }
    },
    selectable: {
        value: false
    }
});
