/**
 * @module "ui/hazard/hazard-list-item.reel"
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component;

/**
 * @class HazardListItem
 * @extends Component
 */
exports.HazardListItem = HazardListItem = Component.specialize(/** @lends HazardListItem.prototype */ {
    _hazard: { value: null },

    hazard: {
        get: function() {
            return this._hazard;
        },
        set: function (value) {
            this._hazard = value;
            this.dispatchOwnPropertyChange("latitudeString", this.latitudeString);
            this.dispatchOwnPropertyChange("longitudeString", this.longitudeString);
        }
    }

});
