/**
 * @module ui/layer/layer-import.reel/layer-import-file-details.reel
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component,
    ImportLayersService = require("logic/mock-service/import-layers-service").ImportLayersService;

/**
 * @class LayerImportFileDetails
 * @extends Component
 */
exports.LayerImportFileDetails = Component.specialize(/** @lends LayerImportFileDetails# */ {
    constructor: {
        value: function LayerImportFileDetails() {

        }
    },
    handleFileMenuMenuAction: {
        value: function (event) {
            var eventData = event.detail.item;
            switch (eventData.value) {
                case "remove":
                    console.log(this.file);
                    ImportLayersService.removeLayer(this.file.layer);
                    this.dispatchEventNamed("fileRemoved", true, true, this.file);
                    break;
            }
        }
    }
});
