/**
 * @module ui/layer/layer-import-list-item.reel
 * @requires montage/ui/component
 */
var Component = require("montage/ui/component").Component,
    UserLayersService = require("logic/mock-service/user-layers-service").UserLayersService;

/**
 * @class LayerUserServiceListItem
 * @extends Component
 */
exports.LayerUserServiceListItem = Component.specialize(/** @lends LayerUserServiceListItem# */ {
    constructor: {
        value: function LayerUserServiceListItem() {

        }
    },

    layer: {
        value: null // Value to be set by this component's owner.
    },

    handleCheckboxAction: {
        value: function () {
            if (this.layer.enabled) {
                UserLayersService.addLayer(this.layer);
            } else {
                UserLayersService.removeLayer(this.layer.id);
            }
        }
    }
});
