/**
 * @module ui/layer-background-list-item.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class LayerBackgroundListItem
 * @extends Component
 */
exports.LayerBackgroundListItem = Component.specialize(/** @lends LayerBackgroundListItem# */ {

    _isEnabled: {
        value: false
    },

    // used in serialization
    isEnabled: {
        get: function () {
            return this._isEnabled;
        },
        set: function (value) {
            if (value !== this._isEnabled) {
                if (value === true && this.application.delegate.baseMap !== this.node) {
                    this.application.delegate.baseMap = this.node;
                }
                this._isEnabled = value;
            }
        }
    },

    // passed in by owner
    _node: {
        value: undefined
    },

    node: {
        get: function () {
            return this._node;
        },
        set: function (value) {
            if (value && value !== this._node) {
                this._node = value;
                if (this.getBinding("isEnabled")) {
                    this.cancelBinding("isEnabled");
                }
                this.defineBinding("isEnabled", {"<-": "isEnabled", source: this._node});
            }
        }
    }

});
