var Component = require("montage/ui/component").Component;

/**
 * @class LayerBackgroundList
 * @extends Component
 */
exports.LayerBackgroundList = Component.specialize(/** @lends LayerBackgroundList.prototype */{

    // Set by owner.
    layers: {
        value: undefined
    },

    // Set by owner.
    hasSettings: {
        value: false
    },

    // Used in serialization.
    _getTitle: {
        value: function (layers) {
            var count = layers ? layers.length : -1;
            return count === 1 ? "1 Layer" :
                count >= 0 ?   count + " Layers" :
                    "";
        }
    }

});
