/**
 * @module ui/legend/legend-item.reel
 */
var Component = require("montage/ui/component").Component,
    Protocol = require("logic/model/protocol").Protocol;
/**
 * @class LegendItem
 * @extends Component
 */
exports.LegendItem = Component.specialize(/** @lends LegendItem# */ {
    constructor: {
        value: function LegendItem() {

        }
    },

    layer: {
        value: null
    },

    itemType: {
        get: function () {
            var itemType;
            if (this.layer && this.layer.legendUrl) {
                itemType = "image";
            } else if (this.layer && this.layer.mapService.protocol === Protocol.ARCGIS) {
                itemType = "list";
            }
            return itemType;
        }
    }
});
