/**
 * @module "ui/map/map-button.reel"
 */
var AbstractButton = require("montage/ui/base/abstract-button").AbstractButton;

/**
 * @class MapButton
 * @classdesc A button used in some temporary user interface in the map. It
 *            needs to be removed once this temporary user interface is not
 *            needed any more.
 * @extends AbstractButton
 */
exports.MapButton = AbstractButton.specialize( /** @lends Button.prototype */ {
    constructor: {
        value: function MapButton() {
            //this.super();
            this.classList.add("contour-MapButton");
        }
    }
});
