/**
 * @module ui/social-video-item.reel
 */
var Component = require("montage/ui/component").Component;


/**
 * @class SocialVideoItem
 * @extends Component
 */
exports.SocialVideoItem = Component.specialize(/** @lends SocialVideoItem# */ {

    constructor: {
        value: function SocialVideoItem() {

        }
    },

    displayVideoPlayer: {
        value: false
    },

    player: {
        value: undefined
    },

    handleAction: {
        value: function () {
            this.videoController.playVideo(this);
        }
    },

    handleViewOnMapButtonAction: {
        value: function () {
            this.application.delegate.centerOnFeature(this.tweet);
        }
    },

    videoController: {
        value: undefined
    }

});
