/**
 * @module ui/tv-browser.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class TvBrowser
 * @extends Component
 */
exports.TvBrowser = Component.specialize(/** @lends TvBrowser# */ {
    constructor: {
        value: function TvBrowser() {

        }
    },

    _isVisible: {
        value: false
    },

    isVisible: {
        set: function (value) {
            this._isVisible = value;
        },
        get: function () {
            return this._isVisible;
        }
    },

    _msnbc: {
        get: function () {
            var self = this;
            return require.async("ui/tv/tv-msnbc.reel").then(function(exports) {
                var pane = new exports.TvMsnbc();
                pane.owner = self;
                pane.defineBinding("isVisible", {"<-": "owner.isVisible"});
                return pane;
            });
        }
    },

    enterDocument: {
        value: function (firstDraw) {
            var self = this;
            if (firstDraw) {

            }
        }
    }


});
