/**
 * @module ui/user-checkbox-row.reel
 */
var Component = require("montage/ui/component").Component,
    PressComposer = require("montage/composer/press-composer").PressComposer;

/**
 * @class UserCheckboxRow
 * @extends Component
 */
exports.UserCheckboxRow = Component.specialize(/** @lends UserCheckboxRow# */ {
    constructor: {
        value: function UserCheckboxRow() {

        }
    },

    showArrow: {
        value: false
    },

    value: {
        value: null
    },

    _pressComposer: {
        value: null
    },

    enterDocument: {
        value: function (firstTime) {
            if (firstTime) {
                this._pressComposer = new PressComposer();
                this.addComposerForElement(this._pressComposer, this.element);
            }
        }
    },

    prepareForActivationEvents: {
        value: function () {
            if (!this._preparedForAction && this._pressComposer) {
                this._pressComposer.addEventListener("press", this, false);
                this._preparedForAction = true;
            }
        }
    },

    handlePress: {
        value: function (event) {
            event.stopPropagation();
            this.value.isEnabled = !this.value.isEnabled;
            this.dispatchEventNamed("checkboxRowCheckedAction", true, true,this.value);
        }
    }
});
