/**
 * @module ui/user-info-browser.reel
 */
var Component = require("montage/ui/component").Component,
    UserInfo = require("ui/user/user-info.reel").UserInfo;

/**
 * @class UserInfoBrowser
 * @extends Component
 */
exports.UserInfoBrowser = Component.specialize(/** @lends UserInfoBrowser# */ {
    constructor: {
        value: function UserInfoBrowser() {

        }
    },

    user: {
        value: null
    },

    _isVisible: {
        value: false
    },

    isVisible: {
        set: function (value) {
            if (value !== this._isVisible) {
                this._isVisible = value;
                if (!this._isVisible) {
                    this.needsDraw = true;
                }
            }
        },
        get: function () {
            return this._isVisible;
        }
    }

});

