/**
 * @module ui/user-info.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class UserInfo
 * @extends Component
 */
exports.UserInfo = Component.specialize(/** @lends UserInfo# */ {
    constructor: {
        value: function UserInfo() {

        }
    },

    user: {
        value: null
    },

    handleDefaultLanguageTextRowPressAction: {
        value: function () {
            this.dispatchEventNamed("openLanguageListAction", true, true);
        }
    },

    handleDefaultBookmarkTextRowPressAction: {
        value: function () {
            this.dispatchEventNamed("openBookmarkListAction", true, true);
        }
    }
});
