/**
 * @module ui/user-timezone-browser.reel
 */
var Component = require("montage/ui/component").Component,
    UserTimezoneInspector = require("ui/user/user-timezone-inspector.reel").UserTimezoneInspector,
    UserTimezoneCountryList = require("ui/user/user-timezone-country-list.reel").UserTimezoneCountryList,
    UserTimezoneCityList = require("ui/user/user-timezone-city-list.reel").UserTimezoneCityList;

/**
 * @class UserTimezoneBrowser
 * @extends Component
 */
exports.UserTimezoneBrowser = Component.specialize(/** @lends UserTimezoneBrowser.prototype */ {
    constructor: {
        value: function UserTimezoneBrowser() {
            var self = this;
        }
    },

    _isVisible: {
        value: false
    },

    _succession: { value: null },

    isVisible: {
        set: function (value) {
            if (value !== this._isVisible) {
                this._isVisible = value;
                if (!this._isVisible) {
                    this.needsDraw = true;
                }
            }
        },
        get: function () {
            return this._isVisible;
        }
    },

    timeZones: {
        value: null
    },

    user: {
        value: null
    }

});

