/**
 * @module ui/user-timezone-city-list.reel
 */
var Component = require("montage/ui/component").Component;

/**
 * @class UserTimezoneCityList
 * @extends Component
 */
exports.UserTimezoneCityList = Component.specialize(/** @lends UserTimezoneCityList# */ {
    constructor: {
        value: function UserTimezoneCityList() {

        }
    },

    // Set by owner
    currentTimezone: {
        value: null
    },

    prepareForActivationEvents: {
        value: function () {
            if (!this._preparedForAction) {
                this.addEventListener("userListItemSelected", this, false);
                this._preparedForAction = true;
            }
        }
    },


    handleUserListItemSelected: {
        value: function () {
            var selectedItem = "";
            if (this.templateObjects.selectList._selectedItem) {
                selectedItem = this.templateObjects.selectList._selectedItem.title;
            }
            if (this.currentTimezone.city != selectedItem) {
                this.currentTimezone.city = selectedItem;
            }
            this.dispatchEventNamed("cityListSelectedAction", true, true, selectedItem);
        }
    }
});
