var Component = require("montage/ui/component").Component,
    ObjectSelector = require("logic/mock-montage-data/object-selector").ObjectSelector;

/**
 * @class UserTimezoneInspector
 * @extends Component
 */
exports.UserTimezoneInspector = Component.specialize(/** @lends UserTimezoneInspector# */ {

    constructor: {
        value: function UserTimezoneInspector() {
            //this.super();
        }
    },

    autoSetLabel: {
        value: null
    },

    //Set by owner
    user: {
        value: null
    },

    _value: {
        value: null
    },

    value: {
        set: function (value) {
            if (value !== this._value) {
                this._value = value;
                if (value.label === value.city) {
                    value.label = "";
                }
            }
        },
        get: function () {
            return this._value;
        }
    },

    handleCountryCityTextRowPressAction: {
        value: function () {
            this.dispatchEventNamed("openCountryListAction", true, true, this.value);
        }
    },

    handleDeleteButtonAction: {
        value: function () {
            this.dispatchEventNamed("deleteTimezoneAction", true, true, this.value);
        }
    }
});
