var Component = require("montage/ui/component").Component,
    PressComposer = require("montage/composer/press-composer").PressComposer;

/**
 * @class UserTimezoneListItem
 * @extends Component
 */
exports.UserTimezoneListItem = Component.specialize(/** @lends UserTimezoneListItem# */ {

    constructor: {
        value: function UserTimezoneListItem() {
        }
    },

    hideElement: {
        value: false
    },

    value: {
        value: null
    },

    handlePress: {
        value: function (event) {
            if (event.targetElement !== this.dragIcon.element) {
                this.dispatchEventNamed("openTimezoneInspectorAction", true, true, this.value);
            }
        }
    },

    prepareForActivationEvents: {
        value: function () {
            this._pressComposer = PressComposer.create();
            this.addComposerForElement(this._pressComposer, this.element);
            this._pressComposer.addEventListener("press", this, false);
        }
    }

});
